package leetcode.src.problems;

public class Problem1839 {

    public static void main(String[] args) {

        System.out.println(longestBeautifulSubstring(""));
    }

    /*
        ('a', 'e', 'i', 'o', 'u')
     */
    public static int longestBeautifulSubstring(String word) {
        int beforeLogtestLength = 0;
        char lastAppendChar = ZERO_CHARACTER;
        StringBuilder currentLongest = new StringBuilder();

        for (char vowel : word.toCharArray()) {
            char beforeRequiredVowel = getBeforeRequiredVowel(vowel);
            if (lastAppendChar == vowel || lastAppendChar == beforeRequiredVowel) {
                currentLongest.append(vowel);
                lastAppendChar = vowel;
                continue;
            }

            if (lastAppendChar == LAST_CHAR_OF_VOWELS) {
                beforeLogtestLength = beforeLogtestLength > currentLongest.length() ? beforeLogtestLength : currentLongest.length();
            }

            lastAppendChar = ZERO_CHARACTER;
            currentLongest.delete(0, currentLongest.length());
            if (vowel == vowels[0]) {
                lastAppendChar = vowel;
                currentLongest.append(vowel);
            }
        }

        if (lastAppendChar == LAST_CHAR_OF_VOWELS) {
            beforeLogtestLength = beforeLogtestLength > currentLongest.length() ? beforeLogtestLength : currentLongest.length();
        }

        return beforeLogtestLength;
    }

    static final char ZERO_CHARACTER = '0';
    static final char LAST_CHAR_OF_VOWELS = 'u';
    static final char[] vowels = new char[]{'a', 'e', 'i', 'o', 'u'};

    public static char getBeforeRequiredVowel(char currentVowel) {
        if (currentVowel == 'a') {
            return vowels[0];
        }

        int index = 0;
        for (char vowel : vowels) {
            if (vowel == currentVowel) {
                break;
            }
            index++;
        }
        return vowels[index - 1];
    }
}
